import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";

const CreatePost = () => {
    const navigate = useNavigate();
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();

    // State to manage the selected image
    const [selectedImage, setSelectedImage] = useState(null);

    const onSubmit = (data) => {
        // Create a FormData object to append data and image
        const formData = new FormData();
        formData.append('title', data.title);
        formData.append('author', data.author);
        formData.append('date', data.date);
        if (selectedImage) {
            formData.append('image', selectedImage);
        }

        // For demonstration, log the formData content (in a real app, you would send this to your API)
        for (let [key, value] of formData.entries()) {
            console.log(key, value);
        }

        // Optionally, navigate or perform any other actions after submission
        // navigate('/some-route');
    };

    const handleImageChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            setSelectedImage(e.target.files[0]);
        }
    };

    // Function to remove the selected image
    const removeImage = () => {
        setSelectedImage(null);
    };

    return (
        <div className="d-flex justify-content-center align-items-center vh-150">
            <form onSubmit={handleSubmit(onSubmit)} className="p-4 border rounded shadow bg-light" style={{ width: '600px' }}>
                <h2 className="mb-4 text-center">Create Post</h2>

                <div className="mb-4">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input
                        type="text"
                        className={`form-control ${errors.title ? 'is-invalid' : ''}`}
                        id="title"
                        {...register('title', { required: 'Title is required' })}
                    />
                    {errors.title && <div className="invalid-feedback">{errors.title.message}</div>}
                </div>

                <div className="mb-4">
                    <label htmlFor="author" className="form-label">Author</label>
                    <input
                        type="text"
                        className={`form-control ${errors.author ? 'is-invalid' : ''}`}
                        id="author"
                        {...register('author', { required: 'Author is required' })}
                    />
                    {errors.author && <div className="invalid-feedback">{errors.author.message}</div>}
                </div>

                <div className="mb-4">
                    <label htmlFor="date" className="form-label">Creation Date</label>
                    <input
                        type="date"
                        className={`form-control ${errors.date ? 'is-invalid' : ''}`}
                        id="date"
                        {...register('date', { required: 'Creation date is required' })}
                    />
                    {errors.date && <div className="invalid-feedback">{errors.date.message}</div>}
                </div>

                <div className="mb-4">
                    <label htmlFor="image" className="form-label">Upload Image</label>
                    <input
                        type="file"
                        className={`form-control ${errors.image ? 'is-invalid' : ''}`}
                        id="image"
                        accept="image/*"
                        onChange={handleImageChange} // Handle image change
                    />
                    {errors.image && <div className="invalid-feedback">{errors.image.message}</div>}
                </div>

                {/* Displaying the image preview with a remove button */}
                {selectedImage && (
                    <div className="mb-4 position-relative">
                        <h5>Image Preview:</h5>
                        <img 
                            src={URL.createObjectURL(selectedImage)} 
                            alt="Selected" 
                            style={{ width: '100%', height: 'auto', borderRadius: '5px' }} 
                        />
                        <button
                            type="button"
                            className="btn btn-danger position-absolute"
                            style={{ top: '10px', right: '10px' }}
                            onClick={removeImage} // Remove the image
                        >
                            &times; {/* Cross symbol */}
                        </button>
                    </div>
                )}

                <div className="d-flex justify-content-end">
                    <button type="submit" className="btn btn-primary me-2">Add Post</button>
                    <div>
                        <button
                            type="button"
                            className="btn btn-secondary me-2"
                            onClick={() => navigate('/dashboard')}
                        >
                            Cancel
                        </button>
                    </div>
                </div>
            </form>
        </div>
    );
}

export default CreatePost;
